import { Component, OnInit } from '@angular/core';
import { AuthService } from '../_services/auth.service';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  public periodicity!: string;
  public timePeriod!: string;
  public csvFile!: File;
  public mae!: number;
  public rmse!: number;
  public showProfile = false;


  constructor(private auth:AuthService ,private http: HttpClient) { }
  user = {localId:"someid",displayName:"somename",email:"somemail"};
  ngOnInit(): void {
    this.auth.canAccess();
    if (this.auth.isAuthenticated()) {
        //call user details service
        this.auth.detail().subscribe({
          next:data=>{
              this.user.localId = data.users[0].localId;
              this.user.displayName = data.users[0].displayName;
              this.user.email=data.users[0].email;
          }
        })
    }
  }

  onFileSelected(event: any) {
    this.csvFile = event.target.files[0];
  }

  onSubmit() {
    if (!this.csvFile || !this.periodicity || !this.timePeriod) {
      alert('Please fill all the fields.');
      return;
    }
    alert('Form submitted!');

    const formData = new FormData();
    formData.append('csvFile', this.csvFile);
    formData.append('periodicity', this.periodicity);
    formData.append('timePeriod', this.timePeriod);
    this.http.post('http://localhost:5000/forecast', formData).subscribe(
      (response: any) => {
        console.log(response);
        this.mae = response.mae;
        this.rmse = response.rmse;
      },
      (error) => console.log(error)
    );
  }

  showProfilePopup() {
    this.showProfile = true;
  }

  hideProfilePopup() {
    this.showProfile = false;
  }
}

<app-navbar></app-navbar>


<!-- The Modal -->
<div class="modal fade" id="profileModal" tabindex="-1" role="dialog" aria-labelledby="profileModalLabel" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="profileModalLabel">Profile</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <p><b>User ID:</b> {{user.localId}}</p>
        <p><b>User Name:</b> {{user.displayName}}</p>
        <p><b>Email:</b> {{user.email}}</p>

      </div>
    </div>
  </div>
</div>

<!-- This is the Upload page -->
<section class="dashboard-block"  >
  <button type="button" class="btn btn-primary" data-toggle="modal" data-target="#profileModal">
    Profile
  </button>
  <div class="container">
      <div class="row ">
          <div class="col dashboard-sec">
            <h2 class="text-center" style="color: #ffffff;">UPLOAD FILE</h2>

              <form class="dashboard-form" (ngSubmit)="onSubmit()">
              <div class="form-group">
                <label for="csvFile">Upload CSV file:</label>
                <input type="file" id="csvFile" name="csvFile" (change)="onFileSelected($event)">
              </div>
              <div class="form-group">
                <label for="periodicity" class="text-center">Periodicity:</label>
                <select id="periodicity" name="periodicity" [(ngModel)]="periodicity">
                  <option value="daily">Daily</option>
                  <option value="weekly">Weekly</option>
                  <option value="monthly">Monthly</option>
                  <option value="yearly">Yearly</option>
                </select>
              </div>
              <div class="form-group">
                <label for="timePeriod">Period:</label>
                <input type="text" id="timePeriod" name="timePeriod" [(ngModel)]="timePeriod" >
              </div>
              <!--<button type="submit" [disabled]="!csvFile">Submit</button>-->
              <button type="submit" onclick="window.location.href = 'https://app.powerbi.com/groups/me/reports/56500613-202f-476e-9ac9-3f58cd0ba96d/ReportSection';">powerbi</button>
            </form>
              <br>
             < <p><u>ACCURACY METRICS</u></p>
              <h6>MAE: {{mae}}</h6> 
              <h6>RMSE: {{rmse}}</h6>
          </div>

      </div>
  </div>
</section>
